mapper.select:
SELECT a.id, a.file_id, a.file_name, a.file_name_num, a.extend_name, a.file_path, a.is_dir, a.belong_type, a.belong_id, a.deleted, a.delete_time, a.create_id, (SELECT real_name FROM t_user WHERE id = a.create_id AND delete_flag = 0 LIMIT 1) AS create_by, a.create_time, a.update_id, (SELECT real_name FROM t_user WHERE id = a.create_id AND delete_flag = 0 LIMIT 1) AS update_by, a.update_time, (SELECT `name` FROM t_project WHERE id = a.belong_id AND delete_flag = 0 LIMIT 1) AS belong_name FROM t_net_disk_file a WHERE a.delete_flag = 0 AND a.belong_id IN (SELECT id FROM t_project WHERE group_id IN (SELECT group_id FROM t_user_mtm_group WHERE user_id = #{user_id} AND delete_flag = 0) AND delete_flag = 0)
AND a.file_name LIKE CONCAT('%', #{query.fileName}, '%')
AND a.file_path = #{query.filePath}
ORDER BY a.create_time ASC LIMIT #{query.offset}, #{query.limit}

SELECT COUNT(*) FROM t_net_disk_file a WHERE a.delete_flag = 0 AND a.belong_id IN (SELECT id FROM t_project WHERE group_id IN (SELECT group_id FROM t_user_mtm_group WHERE user_id = #{user_id} AND delete_flag = 0) AND delete_flag = 0)
AND a.file_name LIKE CONCAT('%', #{query.fileName}, '%')
AND a.file_path = #{query.filePath}

SELECT a.id, a.file_id, a.file_name, a.file_name_num, a.extend_name, a.file_path, a.is_dir, a.belong_type, a.belong_id, a.deleted, a.delete_time, a.create_id, (SELECT real_name FROM t_user WHERE id = a.create_id AND delete_flag = 0 LIMIT 1) AS create_by, a.create_time, a.update_id, (SELECT real_name FROM t_user WHERE id = a.create_id AND delete_flag = 0 LIMIT 1) AS update_by, a.update_time, (SELECT real_name FROM t_user WHERE id = a.belong_id AND delete_flag = 0 LIMIT 1) AS belong_name FROM t_net_disk_file a WHERE a.delete_flag = 0 AND a.belong_id = #{user_id}
AND a.file_name LIKE CONCAT('%', #{query.fileName}, '%')
AND a.file_path = #{query.filePath}
ORDER BY a.create_time ASC LIMIT #{query.offset}, #{query.limit}

SELECT COUNT(*) FROM t_net_disk_file a WHERE a.delete_flag = 0 AND a.belong_id = #{user_id}
AND a.file_name LIKE CONCAT('%', #{query.fileName}, '%')
AND a.file_path = #{query.filePath}
